=:root {
	/* Typography - Enhanced with better scale */
	--font-family: 'Inter', 'Open Sans', -apple-system, BlinkMacSystemFont, sans-serif;
	--font-family-mono: 'SF Mono', 'Monaco', 'Consolas', monospace;
	--font-weight-light: 300;
	--font-weight-normal: 400;
	--font-weight-medium: 500;
	--font-weight-semibold: 600;
	--font-weight-bold: 700;

	/* Improved font scale */
	--font-size-xs: 10px;
	--font-size-sm: 12px;
	--font-size-base: 14px;
	--font-size-md: 16px;
	--font-size-lg: 18px;
	--font-size-xl: 20px;
	--font-size-2xl: 24px;
	--font-size-3xl: 30px;
	--font-size-4xl: 36px;

	/* Legacy font sizes for compatibility */
	--font-size-body: var(--font-size-sm);
	--font-size-label: var(--font-size-base);
	--font-size-input: var(--font-size-base);
	--font-size-header: var(--font-size-xl);
	--font-size-paragraph: var(--font-size-base);
	--font-size-link: var(--font-size-sm);
	--font-size-error: var(--font-size-sm);
	--font-size-subheader: var(--font-size-base);

	/* Enhanced Color Palette */
	--color-white: #ffffff;
	--color-black: #000000;

	/* Neutral grays - more sophisticated */
	--color-gray-50: #f8fafc;
	--color-gray-100: #f1f5f9;
	--color-gray-200: #e2e8f0;
	--color-gray-300: #cbd5e1;
	--color-gray-400: #94a3b8;
	--color-gray-500: #64748b;
	--color-gray-600: #475569;
	--color-gray-700: #334155;
	--color-gray-800: #1e293b;
	--color-gray-900: #0f172a;

	/* Primary brand colors - enhanced coral/orange */
	--color-primary-50: #fef7f0;
	--color-primary-100: #feecdc;
	--color-primary-200: #fcd9bd;
	--color-primary-300: #fdba8c;
	--color-primary-400: #ff8a4c;
	--color-primary-500: #ff6d5a;
	--color-primary-600: #ea580c;
	--color-primary-700: #c2410c;
	--color-primary-800: #9a3412;
	--color-primary-900: #7c2d12;

	/* Accent colors */
	--color-accent-blue: #3b82f6;
	--color-accent-purple: #8b5cf6;
	--color-accent-green: #10b981;
	--color-accent-yellow: #f59e0b;

	/* Semantic colors */
	--color-success: #10b981;
	--color-warning: #f59e0b;
	--color-error: #ef4444;
	--color-info: #3b82f6;

	/* Background colors */
	--color-background: var(--color-gray-50);
	--color-background-alt: var(--color-white);

	/* Form-specific colors */
	--color-card-bg: var(--color-white);
	--color-card-border: var(--color-gray-200);
	--color-card-shadow: rgba(15, 23, 42, 0.08);
	--color-card-shadow-hover: rgba(15, 23, 42, 0.12);

	--color-input-bg: var(--color-white);
	--color-input-border: var(--color-gray-300);
	--color-input-border-hover: var(--color-gray-400);
	--color-input-border-focus: var(--color-primary-500);
	--color-input-text: var(--color-gray-700);
	--color-input-placeholder: var(--color-gray-400);

	--color-label: var(--color-gray-700);
	--color-header: var(--color-gray-900);
	--color-header-subtext: var(--color-gray-500);
	--color-link: var(--color-primary-600);
	--color-link-hover: var(--color-primary-700);

	--color-submit-btn-bg: var(--color-primary-500);
	--color-submit-btn-bg-hover: var(--color-primary-600);
	--color-submit-btn-bg-active: var(--color-primary-700);
	--color-submit-btn-text: var(--color-white);

	--color-required: var(--color-error);
	--color-clear-button-bg: var(--color-gray-400);
	--color-clear-button-bg-hover: var(--color-gray-500);

	/* Test notice colors */
	--color-test-notice-text: var(--color-warning);
	--color-test-notice-bg: #fffbeb;
	--color-test-notice-border: #fed7aa;

	/* Enhanced Border Radii */
	--border-radius-xs: 2px;
	--border-radius-sm: 4px;
	--border-radius-md: 6px;
	--border-radius-lg: 8px;
	--border-radius-xl: 12px;
	--border-radius-2xl: 16px;
	--border-radius-full: 9999px;

	/* Legacy border radius */
	--border-radius-card: var(--border-radius-xl);
	--border-radius-input: var(--border-radius-md);
	--border-radius-clear-btn: var(--border-radius-full);
	--card-border-radius: var(--border-radius-xl);

	/* Enhanced Spacing System */
	--space-0: 0px;
	--space-1: 4px;
	--space-2: 8px;
	--space-3: 12px;
	--space-4: 16px;
	--space-5: 20px;
	--space-6: 24px;
	--space-8: 32px;
	--space-10: 40px;
	--space-12: 48px;
	--space-16: 64px;
	--space-20: 80px;
	--space-24: 96px;

	/* Legacy spacing */
	--padding-container-top: var(--space-6);
	--padding-card: var(--space-6);
	--padding-test-notice-vertical: var(--space-3);
	--padding-test-notice-horizontal: var(--space-6);
	--margin-bottom-card: var(--space-4);
	--padding-form-input: var(--space-3);
	--card-padding: var(--space-6);
	--card-margin-bottom: var(--space-4);

	/* Enhanced Dimensions */
	--container-width: 448px;
	--container-width-sm: 384px;
	--container-width-lg: 512px;
	--submit-btn-height: 48px;
	--input-height: 44px;
	--checkbox-size: 18px;

	/* Enhanced Shadows */
	--shadow-xs: 0 1px 2px 0 rgba(0, 0, 0, 0.05);
	--shadow-sm: 0 1px 3px 0 rgba(0, 0, 0, 0.1), 0 1px 2px 0 rgba(0, 0, 0, 0.06);
	--shadow-md: 0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -1px rgba(0, 0, 0, 0.06);
	--shadow-lg: 0 10px 15px -3px rgba(0, 0, 0, 0.1), 0 4px 6px -2px rgba(0, 0, 0, 0.05);
	--shadow-xl: 0 20px 25px -5px rgba(0, 0, 0, 0.1), 0 10px 10px -5px rgba(0, 0, 0, 0.04);
	--shadow-2xl: 0 25px 50px -12px rgba(0, 0, 0, 0.25);

	/* Colored shadows */
	--shadow-primary: 0 4px 14px 0 rgba(255, 109, 90, 0.25);
	--shadow-primary-lg: 0 10px 25px -5px rgba(255, 109, 90, 0.3);

	/* Legacy shadows */
	--box-shadow-card: var(--shadow-lg);
	--box-shadow-card-hover: var(--shadow-xl);

	/* Animation & Transitions */
	--transition-fast: 150ms ease;
	--transition-normal: 250ms ease;
	--transition-slow: 350ms ease;
	--transition-bounce: 250ms cubic-bezier(0.68, -0.55, 0.265, 1.55);

	/* Specific transitions */
	--transition-colors: color 150ms ease, background-color 150ms ease, border-color 150ms ease;
	--transition-shadow: box-shadow 250ms ease;
	--transition-transform: transform 150ms ease;
	--transition-all: all 150ms ease;

	/* Z-index scale */
	--z-dropdown: 1000;
	--z-sticky: 1020;
	--z-fixed: 1030;
	--z-modal-backdrop: 1040;
	--z-modal: 1050;
	--z-popover: 1060;
	--z-tooltip: 1070;

	/* Effects */
	--opacity-disabled: 0.5;
	--opacity-placeholder: 0.6;
	--blur-sm: 4px;
	--blur-md: 8px;
	--blur-lg: 16px;

	/* Gradients */
	--gradient-primary: linear-gradient(135deg, var(--color-primary-500) 0%, var(--color-primary-600) 100%);
	--gradient-primary-hover: linear-gradient(135deg, var(--color-primary-600) 0%, var(--color-primary-700) 100%);
	--gradient-card: linear-gradient(145deg, rgba(255, 255, 255, 0.9) 0%, rgba(255, 255, 255, 0.7) 100%);

	/* Glassmorphism effect */
	--glass-bg: rgba(255, 255, 255, 0.8);
	--glass-border: rgba(255, 255, 255, 0.2);
	--glass-shadow: 0 8px 32px 0 rgba(31, 38, 135, 0.37);
	--glass-backdrop-filter: blur(8px) saturate(180%);
}

/* Enhanced Form Styles */
.form-container {
	background: var(--color-background);
	min-height: 100vh;
	padding: var(--space-6);
	font-family: var(--font-family);
}

.form-card {
	background: var(--color-card-bg);
	border: 1px solid var(--color-card-border);
	border-radius: var(--border-radius-card);
	box-shadow: var(--box-shadow-card);
	padding: var(--card-padding);
	margin-bottom: var(--card-margin-bottom);
	transition: var(--transition-shadow);
	position: relative;
	overflow: hidden;
}

.form-card::before {
	content: '';
	position: absolute;
	top: 0;
	left: 0;
	right: 0;
	height: 4px;
	background: var(--gradient-primary);
	border-radius: var(--border-radius-card) var(--border-radius-card) 0 0;
}

.form-card:hover {
	box-shadow: var(--box-shadow-card-hover);
	transform: translateY(-2px);
}

.form-input {
	width: 100%;
	height: var(--input-height);
	padding: var(--padding-form-input);
	border: 1px solid var(--color-input-border);
	border-radius: var(--border-radius-input);
	background: var(--color-input-bg);
	color: var(--color-input-text);
	font-size: var(--font-size-input);
	font-family: var(--font-family);
	transition: var(--transition-colors), var(--transition-shadow);
	outline: none;
}

.form-input::placeholder {
	color: var(--color-input-placeholder);
	opacity: var(--opacity-placeholder);
}

.form-input:hover {
	border-color: var(--color-input-border-hover);
}

.form-input:focus {
	border-color: var(--color-input-border-focus);
	box-shadow: 0 0 0 3px rgba(255, 109, 90, 0.1);
}

.form-label {
	display: block;
	color: var(--color-label);
	font-size: var(--font-size-label);
	font-weight: var(--font-weight-medium);
	margin-bottom: var(--space-2);
	line-height: 1.5;
}

.form-submit-btn {
	width: 100%;
	height: var(--submit-btn-height);
	background: var(--gradient-primary);
	color: var(--color-submit-btn-text);
	border: none;
	border-radius: var(--border-radius-input);
	font-size: var(--font-size-base);
	font-weight: var(--font-weight-semibold);
	font-family: var(--font-family);
	cursor: pointer;
	transition: var(--transition-all);
	position: relative;
	overflow: hidden;
}

.form-submit-btn:hover {
	background: var(--gradient-primary-hover);
	box-shadow: var(--shadow-primary);
	transform: translateY(-1px);
}

.form-submit-btn:active {
	transform: translateY(0);
}

.form-submit-btn:disabled {
	opacity: var(--opacity-disabled);
	cursor: not-allowed;
	transform: none;
}

/* Loading animation for submit button */
.form-submit-btn.loading::after {
	content: '';
	position: absolute;
	top: 50%;
	left: 50%;
	width: 20px;
	height: 20px;
	margin: -10px 0 0 -10px;
	border: 2px solid transparent;
	border-top: 2px solid var(--color-white);
	border-radius: 50%;
	animation: spin 1s linear infinite;
}

@keyframes spin {
	0% { transform: rotate(0deg); }
	100% { transform: rotate(360deg); }
}

.form-error {
	color: var(--color-error);
	font-size: var(--font-size-error);
	margin-top: var(--space-1);
	display: flex;
	align-items: center;
	gap: var(--space-1);
}

.form-error::before {
	content: 'âš ';
	font-size: var(--font-size-sm);
}

.form-link {
	color: var(--color-link);
	text-decoration: none;
	font-size: var(--font-size-link);
	transition: var(--transition-colors);
	border-bottom: 1px solid transparent;
}

.form-link:hover {
	color: var(--color-link-hover);
	border-bottom-color: var(--color-link-hover);
}

/* Micro-animations */
.form-input:focus,
.form-submit-btn:hover,
.form-card:hover {
	animation: subtle-bounce 0.3s ease;
}

@keyframes subtle-bounce {
	0%, 100% { transform: translateY(0); }
	50% { transform: translateY(-1px); }
}

/* Dark mode support */
@media (prefers-color-scheme: dark) {
	:root {
		--color-background: var(--color-gray-900);
		--color-card-bg: var(--color-gray-800);
		--color-card-border: var(--color-gray-700);
		--color-input-bg: var(--color-gray-700);
		--color-input-border: var(--color-gray-600);
		--color-input-text: var(--color-gray-100);
		--color-label: var(--color-gray-200);
		--color-header: var(--color-gray-100);
		--color-header-subtext: var(--color-gray-400);
	}
}

/* Responsive design */
@media (max-width: 640px) {
	:root {
		--container-width: 100%;
		--card-padding: var(--space-4);
		--font-size-header: var(--font-size-lg);
	}

	.form-container {
		padding: var(--space-4);
	}
}
/* Enhanced Textarea Styles */
.form-textarea {
    width: 100%;
    height: 120px; /* Increased height */
    min-height: 80px;
    max-height: 250px;
    padding: var(--padding-form-input);
    border: 1px solid var(--color-input-border);
    border-radius: var(--border-radius-input);
    background: var(--color-input-bg);
    color: var(--color-input-text);
    font-size: var(--font-size-input);
    font-family: var(--font-family);
    line-height: 1.5;
    resize: vertical; /* Allow vertical resizing */
    transition: var(--transition-colors), var(--transition-shadow);
    outline: none;
    overflow-y: auto;
}

.form-textarea::placeholder {
    color: var(--color-input-placeholder);
    opacity: var(--opacity-placeholder);
}

.form-textarea:hover {
    border-color: var(--color-input-border-hover);
}

.form-textarea:focus {
    border-color: var(--color-input-border-focus);
    box-shadow: 0 0 0 3px rgba(255, 109, 90, 0.1);
}

/* Auto-expanding textarea */
.form-textarea-auto {
    height: auto;
    min-height: 80px;
    max-height: 300px;
    resize: none;
    overflow-y: hidden;
}

/* Large textarea variant */
.form-textarea-large {
    height: 180px;
    min-height: 120px;
}

/* Small textarea variant */
.form-textarea-small {
    height: 80px;
    min-height: 60px;
}

/* Textarea with character counter */
.textarea-container {
    position: relative;
}

.textarea-counter {
    position: absolute;
    bottom: 8px;
    right: 12px;
    font-size: var(--font-size-xs);
    color: var(--color-gray-400);
    background: var(--color-input-bg);
    padding: 2px 4px;
    border-radius: var(--border-radius-sm);
}

.textarea-counter.limit-approaching {
    color: var(--color-warning);
}

.textarea-counter.limit-exceeded {
    color: var(--color-error);
}

