return items.map(item => {
  // Base URL
  let baseUrl = "https://app.apollo.io/#/people?page=1";

  // Default filters
  let defaultFilters = [
    "contactEmailStatusV2[]=verified",
    "existFields[]=person_title_normalized",
    "existFields[]=organization_domain",
    "personLocations[]=Singapore",
    "personLocations[]=Singapore%2C%20Singapore",
    "sortAscending=true",
    "sortByField=sanitized_organization_name_unanalyzed"
  ];

  // Extract inputs from form (keys with spaces)
  const jobTitles = item.json["Job Titles"] || [];
  const companySizes = item.json["Company Size"] || [];

  // Build query params for job titles
  let titleFilters = jobTitles.map(title => `personTitles[]=${encodeURIComponent(title)}`);

  // Build query params for company sizes
  let sizeFilters = companySizes.map(size => {
    let normalized = size.replace("-", ","); // Apollo wants "1,10"
    return `organizationNumEmployeesRanges[]=${encodeURIComponent(normalized)}`;
  });

  // Combine all filters
  let fullUrl = `${baseUrl}&${[...defaultFilters, ...titleFilters, ...sizeFilters].join("&")}`;

  return {
    json: {
      apolloUrl: fullUrl,
      jobTitles,      // normalized key for output
      companySize: companySizes // normalized key for output
    }
  };
});
